import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'

const App = () => {
  const [userInput, setUserInput] = useState('');
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    if (userInput) {
      axios
        .get(`https://restcountries.com/v3.1/name/${userInput}`)
        .then(response => {
          setCountries(response.data);
        })
        .catch(error => {
          console.log(error);
        });
    } else {
      setCountries([]);
    }
  }, [userInput]);

  const expandInfo = () => {
    alert('expand info')
  }

  const handleChange = event => {
    setUserInput(event.target.value);
  };

  let content;
  if (countries.length > 10) {
    content = <p>Too many matches, specify another filter</p>;
  } else if (countries.length === 1) {
    const country = countries[0];
    const languageList = Object.values(country.languages).map(language => (
      <li key={language}>{language}</li>
    ));

    content = (
      <div>
        <h2>{country.name.common}</h2>
        <p>Capital: {country.capital}</p>
        <p>Area: {country.area} kmÂ²</p>
        <p>Languages:</p>
        <ul>{languageList}</ul>
        <img src={country.flags.png} alt="Flag" style={{ maxWidth: '200px' }} />
      </div>
    );
  } else if (countries.length > 0) {
    content = (
      <ul>
        {countries.map(country => (
          <div className='container'>
          <li key={country.name.common}>{country.name.common}</li><button onClick={expandInfo}>show</button>
          </div>
        ))}
      </ul>
    );
  }

  return (
    <>
      find countries <input onChange={handleChange} />
      {content}
    </>
  );
};

export default App;
