import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [userInput, setUserInput] = useState('');
  const [countries, setCountries] = useState([]);
  const [expandedCountries, setExpandedCountries] = useState([]);

  useEffect(() => {
    if (userInput) {
      axios
        .get(`https://restcountries.com/v3.1/name/${userInput}`)
        .then(response => {
          setCountries(response.data);
          setExpandedCountries([]);
        })
        .catch(error => {
          console.log(error);
        });
    } else {
      setCountries([]);
      setExpandedCountries([]);
    }
  }, [userInput]);

  const handleChange = event => {
    setUserInput(event.target.value);
  };

  const handleExpand = country => {
    if (expandedCountries.includes(country)) {
      setExpandedCountries(expandedCountries.filter(c => c !== country));
    } else {
      setExpandedCountries([...expandedCountries, country]);
    }

    // Make an API call to get weather information
    axios
      .get(`https://api.openweathermap.org/data/2.5/weather?q=${country.capital}&appid=YOUR_API_KEY`)
      .then(response => {
        // Process the weather information response here
        const weatherData = response.data;
        console.log(weatherData);
      })
      .catch(error => {
        console.log(error);
      });
  };

  let content;
  if (countries.length > 10) {
    content = <p>Too many matches, specify another filter</p>;
  } else if (countries.length === 1) {
    const country = countries[0];
    content = (
      <div>
        <h2>{country.name.common}</h2>
        <p>Capital: {country.capital}</p>
        <p>Area: {country.area} kmÂ²</p>
        <p>Languages:</p>
        <ul>
          {Object.values(country.languages).map(language => (
            <li key={language}>{language}</li>
          ))}
        </ul>
        <img src={country.flags.png} alt="Flag" style={{ maxWidth: '200px' }} />
      </div>
    );
  } else if (countries.length > 0) {
    content = (
      <ul>
        {countries.map(country => (
          <li key={country.name.common}>
            {country.name.common}
            <button onClick={() => handleExpand(country)}>
              {expandedCountries.includes(country) ? 'Collapse' : 'Expand'}
            </button>
            {expandedCountries.includes(country) && (
              <div>
                <p>Capital: {country.capital}</p>
                <p>Area: {country.area} kmÂ²</p>
                <p>Languages:</p>
                <ul>
                  {Object.values(country.languages).map(language => (
                    <li key={language}>{language}</li>
                  ))}
                </ul>
                <img src={country.flags.png} alt="Flag" style={{ maxWidth: '200px' }} />

                {/* Display weather information */}
                <p>Weather: Temperature, Weather Icon, Wind Speed</p>
              </div>
            )}
          </li>
        ))}
      </ul>
    );
  }

  return (
    <>
      find countries <input onChange={handleChange} />
      {content}
    </>
  );
};

export default App;
